mapper.update:

UPDATE PRODUCT 
set prod_name = #{PROD_NAME}, prod_category = #{PROD_CATEGORY},
prod_price = #{PROD_PRICE}, prod_stock = #{PROD_STOCK}, 
prod_rprice = #{PROD_RPRICE}, prod_rstock = #{PROD_RSTOCK}, 
prod_date = SYSDATE, PROD_THUMB = #{PROD_THUMB}, PROD_SUB = #{PROD_SUB} 
where prod_num = #{PROD_NUM}
